void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragColor = texture(iChannel0,fragCoord / iResolution.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float t = iTime/4.;

    float s = sin(t/8.), c = cos(t/8.);

    vec2 muv = fragCoord/iResolution.xy-.5;
    muv += .1*sin(muv.yx*vec2(5.5,8.3)+t/2.);
    //muv.y/=iResolution.x/iResolution.y;
    muv *= mat2(s,c,c,-s);
    muv *= smoothstep(0.,6.,t)*3.;

    muv = abs(muv);

    muv = fract(abs(muv-1.));

    vec4 last = texture(iChannel0,muv);

    vec4 new = .07/length(muv-vec2(.4+.05*s,.5+.05*c))*vec4(uv,1,1);

    fragColor = last*min(.6,t/5.)+new;
}
