float rayPlane(vec3 ray, vec3 pos, vec3 norm)
{
    return dot(pos, norm) / dot(ray, norm);
}

void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 final = vec3(0);

    // set up and transform camera
    vec3 camera = vec3(0, 0, 4);
    vec3 screenPos = vec3(fragCoord / iResolution.y, 3) - vec3(iResolution.x / iResolution.y / 2., 0.5, 0);

    float camAngle = 6.28 * iMouse.x / iResolution.x;
    pR(camera.xz, camAngle);
    pR(screenPos.xz, camAngle);

    vec3 ray = normalize(screenPos - camera);

    // set up local coords of the plane
    vec3 norm = vec3(0, 0, -1);
    vec3 up = vec3(0, 1, 0);
    vec3 right = vec3(1, 0, 0);
    float angle = iTime / 1.;

    pR(norm.xz, angle);
    pR(right.xz, angle);

    // ray trace
    float t = -1.;
    float r = rayPlane(ray, vec3(0, 0, 0) - camera, norm);
    vec3 pos = camera + r * ray;
    float x = dot(pos, right);
    if (-1. < x && x < 1. &&
        -1. < pos.y && pos.y < 1.)
        t = r;

    float alpha = 0.;
    vec3 planeFinal = vec3(1,1,1);
    vec3 background = texture(iChannel1, ray).xyz;

    if (t > 0.0)
    {
        vec2 uv = vec2(x, pos.y);
        uv.y *= -1.;
        int c = 211;
        vec2 u = vec2( c % 16, 15-c / 16 )/16.;
        u += uv / 8.;

        const float smoothing = 1. / 32.;
        float s = texture(iChannel0, u).w;
        alpha = smoothstep(0.5 - smoothing, 0.5 + smoothing, s);

        // color the square
        vec3 r = reflect(ray, norm);
        planeFinal = texture(iChannel2, r).xyz + 0.3*texture(iChannel3, uv).xyz;
    }

    final = mix(background, planeFinal, alpha);

    fragColor = vec4(final, 1.0);
}
