// variant of https://shadertoy.com/view/lsVczV


#define mainImage(O,u)                                                 \
    vec2 U = fract( u/iResolution.y* vec2(3,6) * mat2(1,-1,1,1) ) -.5, \
    V = abs( U * mat2(.9,.16,.16,.9) );                                \
    O += .6 - cos ( 20.*max(V.x,V.y)  + .7*texture(iChannel0,U/2.).r )




