#define SUNSET vec3(0.9, 0.3, 0.3)

float circle(in vec2 _st, in float _radius){
    vec2 dist = _st-vec2(0.5)*texture( iChannel0, vec2(_st.x,_st.y) ).x;
	return 1.-smoothstep(_radius-(_radius*0.01),
                         _radius*texture( iChannel0, vec2(_st.x,_st.y) ).y+(_radius*0.01),
                         dot(dist,dist)*4.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy / iResolution.x;

    vec3 color1 = vec3(circle(st,0.5*texture( iChannel0, vec2(st.x,st.y) ).x));
    vec3 color = mix(color1, SUNSET, 0.5);
    fragColor = vec4(color, 1.0);
}
