float st2(vec2 p){
	float s;
    float ss = 2.0;
    float f = 90.0;

   	p += floor(p * (f));

    s = sin(p.x + iTime * ss) * sin(p.y + iTime * ss);

    return s*10.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;



   	fragColor =  texture( iChannel0, uv + st2(fragCoord.xy*2.0 ) / iResolution.xy);


