float gtime;

const float pi = 3.141592653589793;

float paramA, paramB, paramC;

float beat (float time, float freq, float attack, float decay, float paramB, float paramC) {
  if (time<=0.) return 0.;
  float b = 0.;
  for (float i = 1.; i < 10.; i+=0.5) {
    vec4 rnd = texture(iChannel0, vec2(sin(i/10.+gtime*paramB),0.5));
    b += 0.5*sin(rnd.a*paramC+time*freq*pi*2.*float(i))/float(i)*rnd.x;//*
  }
  return (1.-sin(time*attack*pi)/(time*attack*pi))*b*exp(-time*decay);
}

vec2 mainSound( float time )
{
    float v = 0.;
    time *= 1.;
    float k = 100.;
    time *= 0.5;
    time += texture(iChannel0, vec2(time*0.01, 0.)).z;
    gtime = time;
    time += texture(iChannel0, vec2(time*0.01, 0.)).z;

    v += 0.25*beat(mod(time-0.00,0.125),         30., 1000., 30., 0.4, 100.);
    v += 0.25*beat(mod(time-.125*1.,1.),         200., 100., 10., 30., 5000.)*0.5;
    v += 0.25*beat(mod(time-.125*5.,1.),         1000., 100., 10., 20., 50000.)*0.5;
    v += 0.25*beat(mod(time-.125*1.,1.),         2., 100., 1., 10., 5000.)*0.5;

    return vec2(v);
}

