void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = vec4(1.0);

    float value = texelFetch(iChannel0, ivec2(fragCoord.x, 0), 0).a;
    if (uv.y * 2.0 - 1.0 <= value) {
       fragColor.xyz = vec3(0.0);
    }
}

float gDamping = 0.99;

float gC = 1.0;
float gP = 1.0; // time step
float gQ = 1.0; // spatial step


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // simulation using 2 steps previous frames
    if (fragCoord.y == 0.5) {

        //  t
        // /|\        f_12
        //  |   f_01, f_11, f_21
        //  |         f_10
        //   -----------------> x

        float f_01 = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(-1, 0), 0).a +
                     texelFetch(iChannel1, ivec2(fragCoord) + ivec2(-1, 0), 0).a;

        float f_11 = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(0,  0), 0).a +
                     texelFetch(iChannel1, ivec2(fragCoord) + ivec2(0,  0), 0).a;

        float f_21 = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(1,  0), 0).a +
                     texelFetch(iChannel1, ivec2(fragCoord) + ivec2(1,  0), 0).a;

        float f_10 = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(0,  1), 0).a;

        fragColor.a =
            (gC*gC*gP*gP/(gQ*gQ)) * (f_01 - 2.0 * f_11 + f_21) + 2.0 * f_11 - f_10;
        fragColor.a *= gDamping;

    // progress previous frame
    } else if (fragCoord.y == 1.5) {
        fragColor.a = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(0, -1), 0).a;
    }
}

float gBroadness = 1.0;
float gStrength = 0.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor.a = 0.0;
    if (fragCoord.y == 0.5 && iMouse.w > 0.5) {
        if (distance(fragCoord.x, iMouse.x) <= gBroadness) {
          float value = (iMouse.w / iResolution.y) * 2.0 - 1.0;
          fragColor.a = value * gStrength;
        }
    }

    // Fake input for better thumbnail
    if (fragCoord.y == 0.5 && float(iFrame) < 1.0) {
        if (fragCoord.x >= iResolution.x / 2.0 &&
            fragCoord.x < iResolution.x / 2.0 + gBroadness * 2.0) {
            fragColor.a = 0.4;
        }
    }
}
