// cf https://www.desmos.com/calculator/y40lzzbov2
// used here https://www.shadertoy.com/view/llVXzd

#define S(s,d)  sin( mod(-atan(U.y,U.x) s 3.14, v) d )

void mainImage( out vec4 O,  vec2 U )
{
    U = (U+U -(O.xy=iResolution.xy)) / O.y;
    float N = 5.+2.*(floor(U.x)+2.),
          v = 2.*6.28/N;

    if (U.y>0.) U.x-=.2;
    U = 2.*fract(U) -1.;

    O +=  step( length(U), .2 / min(max(S(+,),S(+,+v)),
                                    max(S(-,),S(-,+v))) ) -O;
}
