void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    //uv = 1.0 + 2.0 * uv;
    //uv.x *= iResolution.x / iResolution.y;
    float mover = texture( iChannel0, vec2( 1.0, 0.0 ) ).x;
    vec4 m = iMouse / iResolution.xxxx;
    vec2 center = vec2( m.x, m.y );
    vec2 centerOne = vec2( sin( iTime ), cos( iTime ) );
    float dist = length( uv - center);
    float distOne = length( uv - centerOne );
    float decay = 0.41;
    float speed = 83.2;
    float triggy = 0.0;
    float maxiter = 5.0;
    float amplitude = 2.0;
    vec3 trigger = vec3( 0.0 );
    vec3 colour = vec3( 0.0 );
    if( trigger.x == 0.0 ){
        trigger = vec3( exp( sin( (dist + distOne) * speed * mover ) * -decay ) * 
        sin( ( dist + distOne ) * speed * mover  ) * amplitude, 0.0, 0.0 );
    }

    fragColor = vec4( trigger.x, 0.3, 1.0, 1.0 );
}
