void mainImage( out vec4 f, vec2 w )
{
	vec4 q = vec4(w,iTime*.15,1.) / iResolution.xyzx;
    for(float i=1.; i<30.; i++)
        q.w += smoothstep( .15, .0, length(q.xy - vec2(i/30. + sin(i + q.z), .5 + .3 * sin(q.x * 9. * sin(q.z) + q.z))));
    f = vec4(1. - q.w) * 4.*sqrt(q.x*q.y*(1.-q.x)*(1.-q.y));
}
