void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 fb = texture(iChannel0, uv) ;
    if(fragCoord.x < iResolution.x/2.0){
    	fragColor = texture(iChannel1, uv + (fb.rg * fb.b));
    } else{
        fragColor = texture(iChannel0, uv+vec2(-0.5,0.0));
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy; 
    vec2 coord = uv;


    //rotation
    /*
    float sin_factor = sin(iTime*0.00001);
    float cos_factor = cos(iTime*0.00001);
    coord = vec2((coord.x - 0.5), coord.y - 0.5 ) * mat2(cos_factor, sin_factor, -sin_factor, cos_factor);

    coord += 0.5;
    */

    if(iFrame < 10 || iMouse.z >0.0) {
    	fragColor = texture(iChannel1, coord);
    } else {
        coord = -1.0 + 2.0 * coord;
        coord *= 0.995;
        //coord.y += 0.001;
        vec2 step = 1.25/iResolution.xy;
        vec3 r = texture(iChannel0, coord*0.5 + 0.5).rgb;

        //r+= 0.001;
        r.r += r.g*0.001;
        r.g += r.b*0.001;
        r.b += r.r*0.001;
        r = mod(r, vec3(1.0));

    	fragColor = vec4(vec3(r), 1.0);
    }
}
