void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p=uv-.5;
    p.x*=iResolution.x/iResolution.y;
    float l=length(p);
    uv+=p/l*abs(sin(l*9.-iTime*2.));
    vec3 c=vec3(.01/length(fract(uv)-.5)/l);
    fragColor=vec4(.01/abs(fract(uv)-.5),0.,1.);
}
