const vec2 GRID_DIMS = vec2(50, 50);
const vec4 COL_EVEN = vec4(1.0, 1.0, 1.0, 1.0);
const vec4 COL_ODD = vec4(0.0, 0.0, 0.0, 1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 m = mod(fragCoord, 2.0 * GRID_DIMS);
	if (m.x < GRID_DIMS.x && m.y < GRID_DIMS.y ||
        m.x >= GRID_DIMS.x && m.y >= GRID_DIMS.y)
        fragColor = COL_EVEN;
    else
        fragColor = COL_ODD;
}
