// Author: ocb
// Title: Phot'on stage

void mainImage( out vec4 O, in vec2 U )
{
    vec2 uv = U/R;
    vec3 col =  T1(uv).rgb + T2(uv).rgb;	// drawing lights and rays
    vec2 h = T0(vec2(0.)).xy;	// retrieving sensitivity and speed

    float size = smoothstep(-.15,-.12,-abs(uv.y-.75));	// drawing sliders
    float blu = (smoothstep(-.003,0.,-abs(uv.x-.05))
                +smoothstep(-.003,0.,-abs(uv.x-.08))
                )*size;

    col = mix(col,vec3(0.,0.,1.), blu);

    // drawing slider's level
    col += vec3(.2,.2,.5)
           *(.01/length(uv-vec2(.05,h.x))+.01/length(uv-vec2(.08,h.y)));

    O = vec4(col,1.0);
}

// Mouse contrôle for sensitivity (h.x) and speed (h.y)

void mainImage( out vec4 O, in vec2 U )
{
    vec2 init = vec2(.75,.75);

    vec2 uv = U/R;
    vec2 m = iMouse.xy/R;
    float start = step(1.,float(iFrame));

    vec2 h = start*(T0(uv).rg-init)+init;

    float size = step(abs(m.y-.75),.15);
    h.x += (m.y-h.x)*step(abs(m.x-.05), .01 )*size;
	h.y += (m.y-h.y)*step(abs(m.x-.08), .01 )*size;

    O = vec4(h,0.,1.0);
}

// create the lights grid depending on sensitivity

vec2 grid(in vec2 uv, in float x){ uv *= x; return uv - .5*sin(uv);}
vec2 edge(in vec2 x){ return abs(fract(x)-.5);}

void mainImage( out vec4 O, in vec2 U )
{
	vec2 uv = U / R;

    float svty = 2.*T2(vec2(0.)).x-1.; // sensitivity from buff A

    // get music signal on 2 freq. (15 and 400)
    float amp = texelFetch(iChannel1,ivec2(15,0),0).x +texelFetch(iChannel1,ivec2(400,0),0).x;

    float t=iTime*.01;

    // create grid (3 grids intersecting)
    vec2 a = grid(uv,10.), b = grid(uv,25.), c = grid(uv,50.);
    // move grid
    vec2 p = floor(a)/10.+t, q = floor(b)/25.+.7*t, s = floor(c)/50.+1.3*t;

    // find grid edge to decrease lights on border
    vec2 bp = ( edge(a) + edge(b) + edge(c) )/1.5;
    bp*=bp*bp;

    vec4 tex = .8*T0(p)+.6*T0(q)+.4*T0(s); 
    tex -= .2*(bp.x+bp.y);  // decreasing light
    tex *= 2.*smoothstep(1.6-svty*amp,1.8,tex.r)*tex;

	O = tex;
}

// create light rays
float H2 (in vec2 st) { 						
    return fract(sin(dot(st,vec2(12.9898,8.233))) * 43758.5453123);
}

void mainImage( out vec4 O, in vec2 U )
{
	vec2 uv = U/R;
	vec3 col = vec3(0.);
    vec2 light = light(iTime);
    vec2  d = (.3*light-uv)/32.;
    float w = .05;
    vec2  s = uv+d;
    for( int i=0; i<32; i++ )
    {
        col += w*T0(s).rgb;
        w *= .7;
        s += d;
    }

	O = vec4( col, 1.0 );
}

// Propagate lights ray

// Increase speed range to slow down more the flux
// for fast computer.
#define SpdRange 40.

// Hash function is used for dithering
// to avoid banding
float H2 (in vec2 st) { 						
    return fract(sin(dot(st,vec2(12.9898,8.233))) * 43758.5453123);
}

void mainImage( out vec4 O, in vec2 U )
{
	vec2 uv = U/R;
	vec3  col = T0(uv).rgb;
    vec2 light = light(iTime);
    float spd = (.95-T2(vec2(0.)).y)*SpdRange;
    vec2  d = (.3*light-uv)/spd;
    float w = .98;
    vec2  s = uv+d*H2(uv*iTime);

    col += w*T1(s).rgb;

	O = vec4( col, 1.0 );
}
