void mainImage( out vec4 c, in vec2 f ) {
	vec2 
        v = f.xy / iResolution.xy - .5,
        m;

    c *= 0.;

    v.y = pow(.75 + v.y, 2.5);
    v.x = v.y * abs(v.x) * 2.;

    m = mod(v + vec2(0., iTime / 9.), .1);

    if (
        m.x < .005 ||
        m.y < .005
    ) c = (1. - m.x * 22.) * abs(sin(1.8 * v.y)) * vec4(1., .0, .5, 1.) ;
}
