void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 	vec2 uv = fragCoord.xy / iResolution.xy;

  	vec4 final_color = vec4(0.);  
 	float weight0 = iMouse.x/iResolution.x;
	float weight1 = iMouse.y/iResolution.y;
	float weight2 = 1.0 - weight0 - weight1;
	final_color = texture(iChannel0,   uv) * weight0 +
        texture(iChannel1, uv) * weight1 +
        texture(iChannel2, uv) * weight2;   
    float dist = distance(uv, vec2(0.5)),
    falloff = iMouse.y < 0.01 ? 0.1 : iMouse.y / iResolution.y,
    amount = iMouse.x < 0.01 ? 1.0 : iMouse.x / iResolution.x; 
    final_color *= smoothstep(0.8, falloff * 0.8, dist * (amount + falloff));    

	fragColor = final_color;
}
