#define t iChannelTime[0]/4.
#define f(z) ( length(mod(z,2.)-1.+.13*sin(sin(4.65*(t-.4))*38.*z))-.5 )

#define mainImage(c,p) vec3 d=.6-vec3(p+vec2(-130,40),1)/iResolution.y,o=d; for(int i=0;i<99;++i) o+=f(o)*d; c.xyz = vec3(sin(t),step(5.45,t)*.1,.1*cos(t))*f(o-d)*(9.-o.z);

//thanks to theGiallo for the mainImage trick

