void mainImage(out vec4 O,vec2 U)
{
    O.xyz = iResolution;
    U = 2.*U / O.y - 1.;
    O = vec4( U.x + U.y < .5
             ? smoothstep((O.z = length(fract(U * O.x * .004) - .5))+ .02, O.z, .4)
             : ceil(sin(U.y*80.)));
}



