vec3 ColorHSV(float h, float s, float v)
{
  float Vmin, a, Vinc, Vdec;
  int Hi;

  if(h > 360.0)
    h = 360.0;
  if(s > 100.0)
    s = 100.0;
  if(v > 100.0)
    v = 100.0;

  Hi = int(h / 60.0) % 6;
  Vmin = ((100.0 - s) * v) / 100.0;
  a = (v - Vmin) * float((int(h) % 60)) / 60.0;
  Vinc = Vmin + a;
  Vdec = v - a;

  v *= 2.55;
  Vmin *= 2.55;
  Vinc *= 2.55;
  Vdec *= 2.55;

  if (Hi == 0)
    return vec3(v, Vinc, Vmin);
  if (Hi == 1)
    return vec3(Vdec, v, Vmin);
  if (Hi == 2)
    return vec3(Vmin, v, Vinc);
  if (Hi == 3)
    return vec3(Vmin, Vdec, v);
  if (Hi == 4)
    return vec3(Vinc, Vmin, v);
  if (Hi == 5)
    return vec3(v, Vmin, Vdec);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	float c = cos(iTime);
    float s = sin(iTime);	
	vec2 metaballs[4];

	metaballs[0] = vec2(c * 200.0 + 300.0, s * 150.0 + 200.0);
	metaballs[1] = vec2(c * 250.0 + 350.0, sin(iTime * 0.25) * 100.0 + 200.0);
	metaballs[2] = vec2(cos(iTime * 0.33) * 300.0 + 350.0, sin(iTime * 1.5) * 150.0 + 200.0);
	metaballs[3] = vec2(iMouse.xy);

	fragColor = vec4(ColorHSV(4000.0/distance(fragCoord.xy,metaballs[0])
	         	   + 4000.0/distance(fragCoord.xy,metaballs[1])
	         	   + 4000.0/distance(fragCoord.xy,metaballs[2])
	         	   + 6000.0/distance(fragCoord.xy,metaballs[3]), 100.0, 100.0), 1.0);	
}
