void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    uv -= .5;


    //uv.x *= iMouse.x * .005;
    uv.x *= cos(iTime + uv.y  ) * 2.;
 	uv.y *= sin(iTime + uv.x ) * 2.;
    //uv.y *= iMouse.y * .005;



    float d = length(uv);
    float g = length(atan( d * iTime) * .4);
 	float o = 25.f;



    vec3 col = vec3(smoothstep(g, g - .1, abs(sin(g * iTime * o )) ), 
                    smoothstep(g, g - .1, sin(g * iTime * o)), 
                    smoothstep(g, g - .1, sin(g * iTime * o) ));

    d = smoothstep(0.04, 0.42, d);

    fragColor = vec4(col * d,1.0);
}
