// based on https://www.shadertoy.com/view/ltjXWW

#define L  20.
#define R(a) mat2(C=cos(a),S=sin(a),-S,C)
float C,S,v, T;

float N(vec2 u) { // infinite perlin noise with constant image-space spectrum (like Shepard scale)
	mat2 M = R(1.7);
    float v = 0., t = 0.;
	for (float i=0.; i<L; i++)
	{   float k = i-T,
		      a = 1.-cos(6.28*k/L),
		      s = exp2(mod(k,L));
		v += a/s* ( 1. - abs( 2.* texture(iChannel0, M*u*s/1e3 ).r - 1.) ); 
		t += a/s;  M *= M;
	}
    return v/t;
}

void mainImage( out vec4 O, vec2 U ) {
	vec2  R = iResolution.xy; 
          U = (U-.5*R)/R.y;
    T = 1.5*iTime;
    float e = .5*iMouse.y/R.y;
  //float e = U.x < 0. ? 0. : .03;

    O = vec4 ( sqrt(smoothstep(.7-e,.7+e, N(U) )) );  // NB: sqrt approximates gamma 1./2.2
}
