vec3 en(vec3 p)
{
    return p*texture(iChannel1, p.xy).r;
}
float star(vec3 p, float d)
{
    return length(sqrt(sin(en(p))*2.0))-length(texture(iChannel1, p.yx).rgb);
}
float rand2f(float x)
{
    return cos(sin(dot(x, 2.3465))*min(x, 5.24156));
}
float trace(vec3 ro, vec3 rd)
{
    float t = 0.0;
        for(float i = 0.0; i < 24.0; i++)
        {
            vec3 p = ro+t*rd;
            float d = star(p, 0.5);
            t += d * 0.5;
        }
    return t;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float X = iMouse.x / iResolution.x / 2000.0;
    float Y = iMouse.y / iResolution.y / 2000.0;
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0 + texture(iChannel1, uv).r;
    uv.x *= iResolution.x / iResolution.y;
    vec3 rd = normalize(vec3(uv, 1.0));
    rd.xz *= mat2(cos(iTime/2.0),-sin(iTime/2.0),sin(iTime/2.0),cos(iTime/2.0));
    vec3 ro = vec3(rand2f(iTime+0.5)+iTime+0.5,
                   rand2f(iTime)+iTime,
                   rand2f(iTime+1.0)+iTime+1.0);
    float t = trace(ro, rd);
    float fog = 1.0/(1.0+t*t*0.1);
    vec3 fc = vec3(fog);
	fragColor = vec4(fc,1.0)/texture(iChannel2, uv).r;
}
