const float sc = 10000.0; // height of the viewport, it covers 20,000 km 

const float earthRad = 6356.8; // km
const float issElev = 435.0;   // km


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = sc * (-iResolution.xy+2.0*fragCoord) / iResolution.y;
    float px = sc*2.0/iResolution.y;

    // background
    vec3 col = vec3(0.0);

    // earth
    float d = length(p)-earthRad;
    col = mix( col, vec3(0.4,0.5,0.7), 1.0-smoothstep(-px,px,d ) );

    // iss (made it 100km radius so it'd be visible)
    vec2 iss = (earthRad+issElev) * vec2(sin(iTime),cos(iTime));
    d = length(p-iss)-100.0;
    if( d<0.0 )
        col = vec3(1.0);

	fragColor = vec4(col,1.0);
}
