// see Buf A for relevant code

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 reso = iResolution.yy; // need square size

    if (iFrame < 32) {
        // initial frames display the original texture
    	vec2 uv = fragCoord.xy / reso;
    	fragColor = texture(iChannel1, uv);
    } else if (mod(float(iFrame), 16.) > 0.) {
        // repeat the same frame 15 times (for humans)
    	vec2 uv = fragCoord.xy / iResolution.xy;
    	fragColor = texture(iChannel0, uv);
    } else {
        // apply Arnold's cat map (see https://twitter.com/inversed_ru/status/932630822494130176 )
    	vec2 uv = mod(vec2(fragCoord.x * 2. + fragCoord.y, fragCoord.x + fragCoord.y), reso); 
    	fragColor = texelFetch(iChannel0, ivec2(uv), 0);
        // texelFetch is used to get the exact pixel without any risk of bilinear interpolation
    }
}
