void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (fragCoord.xy * 2. - iResolution.xy) / iResolution.y;

	float evo = sin(iTime*.1);
	float a = floor(atan(p.x,p.y)/3.14159*20.)*3.*evo;

	float d = length(p) - cos(a)*0.288;

	d = smoothstep(0.,1.,.8 - d);
	d = smoothstep(0.,0.01/d,d);
	fragColor = vec4(d);
}
