#define time iTime


highp float hash(highp vec2 p){ 

return smoothstep(0.0,0.9,fract(cos(p.x*12.10+p.y*35.10)*334.552));

}


highp float noise( highp vec2 p )
{
    highp vec2 i = floor( p*1.2 );
    highp vec2 f = fract( p*1.2 );
	highp vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( hash( i + vec2(0.0,0.0) ), hash( i + vec2(1.0,0.0) ), u.x),mix( hash( i + vec2(0.0,1.0) ), hash( i + vec2(1.0,1.0) ), u.x), u.y);
}


highp float fbm(highp vec2 p){

highp float set = 1.0;
highp float set2 = 1.85;
highp float clouds = 0.0;

for(int i = 0; i < 8; i++){
set = set*set2;
clouds += abs(clamp(noise(p),0.0,1.0)) / set;
set = set / set2;
set *= 2.5;
p *= 2.0;
}
	return clouds;
}


float sun(float x,float y,float t){
   float xx  =x + sin(t);
   float yy = y + cos(t);
   return 1.0/sqrt(xx*xx+yy*yy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

   vec2 p =(fragCoord.xy/iResolution.x)*2.0-vec2(1.0,iResolution.y/iResolution.x);

   float a = sun(p.x,p.y,time);

   vec4 sun = vec4(2.0,2.0,0.2,1.0)*(a/10.0);

   vec4 sky = vec4(0.23,0.42,1.0,1.0);

   vec2 p2 = fragCoord.xy / iResolution.xy;

   highp float col = fbm(p2*5.0);

   fragColor = sun + mix(sky,vec4(1.0)*col,min(max(smoothstep(0.5, 1.0, col),0.0),1.0));

}
