void mainImage( out vec4 c, vec2 f )
{
    vec4 g,p;

    f=(f-(c.xy = iResolution.xy)*.5)/c.x*2.3;
    c=vec4(.0,.08,.16, 1.);

    for(float i=.4;i<.6;i+=.0005)
        p=vec4(f,sqrt(1.-dot(f/i,f/i)),1.),
        g=texture(iChannel0, .02*iTime+p.xy*.2/p.z), 
        g.r > i ? c = g : c;

    c*=max(0.,p.y+p.z+p.x);
    c.b+=.04*(1.-c.a)*(1.+p.y);
}


