void mainImage( out vec4 f, in vec2 g ) {
    //setting the previous frame pixel color to f
    f = texelFetch(iChannel0, ivec2(g),0);

    //if the length from mouse is lower than length from previous mouse positions, set this length to closest and set a random color
	float o = 1e4-length(iMouse.xy-g);
    if (f.a < o&&iMouse.z>.0) {
        f = vec4(fract(sin(iTime*vec3(1,2,3))*9.)-step(1e4-o,10.),o);
    }
}
