//Ethan Alexander Shulman 2018

#define OUTLINE_COLOR vec4(.2)
#define OUTLINE_STRENGTH 20.
#define OUTLINE_BIAS -.5
#define OUTLINE_POWER 1.

#define PRECISION 6.

void mainImage(out vec4 o, in vec2 u) {
    vec2 r = iResolution.xy;
    vec4 p = pow(texture(iChannel0,u/r),vec4(2.2)),
        s = pow(texture(iChannel0,(u+.5)/r),vec4(2.2));
    float l = clamp(pow(length(p-s),OUTLINE_POWER)*OUTLINE_STRENGTH+OUTLINE_BIAS,0.,1.);
    p = floor(pow(p,vec4(1./2.2))*(PRECISION+.999))/PRECISION;
    o = mix(p,OUTLINE_COLOR,l);
}
