#define mi 256.

void 
mainImage( out vec4 oCol, in vec2 iCoord )
{
	vec2
    uv = iCoord.xy / iResolution.xy,
    dx = vec2( 1. / iResolution.x, 0. ),
    dy = vec2( 0., 1. / iResolution.y );

    float
    t = .5 + .5 * sin( iTime ),
    c   = texture( iChannel0, uv ).r / mi,
    dxm = texture( iChannel0, uv - dx ).r,
    dxp = texture( iChannel0, uv + dx ).r,
    dym = texture( iChannel0, uv - dy ).r,
    dyp = texture( iChannel0, uv + dy ).r;

    vec2
    col = .5 + .5 * vec2( sign( dxp - dxm ), sign( dym - dyp ) );

    oCol = vec4( dot( col.gr, col.rg ) * .8 + .2 * abs( vec3( sin( 2. + .05 * iTime ), sin( 3. + .03 * iTime ), sin( 5. + .02 * iTime ) ) ), 1. );
}

#define mi 256.

float
mb( vec2 c, float r2 ) {

    vec2
    z = c,
    d = z * z;

    for( float i = 0.; i < mi; ++ i ) {

        if( r2 < d.x + d.y )

            return i;

        z = vec2( d.x - d.y, 2. * z.x * z.y ) - c;

        d = z * z;

    }

    return mi;
}

void
mainImage( out vec4 oCol, in vec2 coord ) {

    vec2
    uv = 2. * coord.xy / iResolution.xy - 1.;

    uv *= vec2( iResolution.x / iResolution.y, 1. );    

    float
    c = cos( .3 * iTime ),
    s = sin( 2.115 * iTime ),
    t = sin( 3. * iTime ) * cos( .5 * iTime );

    uv *= 10. * pow( .0000001, ( .5 + .5 * sin( .2 * iTime ) ) );

    uv = vec2( c * uv.x - s * uv.y, s * uv.x + c * uv.y );

    uv += vec2( 1.39005, 0.114548 );

    oCol = vec4( 64. * log2( mb( uv, mi * .1 * ( 1. + t ) ) ), 0., 0., 1. );
}
