#define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//coordinate system
    vec2 pixel = fragCoord.xy / iResolution.y;

    //wave parameters
    vec2 dotFrequency = vec2(12.0, 7.0) * PI;

    vec2 offsetAmplitude = vec2(2.0 * sin(iTime));
    vec2 offsetFrequency= vec2(2.0) * PI;
    vec2 offsetVelocity= vec2(1.0);

    //offset equation
    vec2 offset= offsetAmplitude * sin( offsetFrequency * pixel + (offsetVelocity * iTime));


    //y-component of visual
    float value= sin( pixel.y * dotFrequency.y + offset.x) / 2.;

    //x-component of visual
    value+= sin( pixel.x * dotFrequency.x + offset.y) /2.;

    //boundary parameters
    float threshold= 1.3 * sin((0.5 * iTime) + ( 0.5 * pixel.x));
    float epsilon= 0.3;

    vec3 color;

    if (value > threshold + epsilon) {
        color= vec3( 0.5 , 0.5 + sin(iTime ) / 2.0, 0.0);
    }
    else if (value < threshold - epsilon) {
        color= vec3(0.5 + sin(iTime) / -2.0, 1.0 , 0.5 + sin(iTime) / 2.0);
    }
    else {
        color= vec3(0.5 + sin(iTime) / -2.0, 0.0 , 0.5);
    }

	fragColor = vec4( color, 1.0);
}
