void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec4 color_bl = vec4(0.0,0.0,0.0,0.0);
    vec4 color_wt = color_bl + 1.0;

    vec2 xy = fragCoord.xy / iResolution.xy; // Condensing this into one line
    vec2 mxy = iMouse.xy / iResolution.xy;
    vec4 texColor = texture(iChannel0,xy); // Get the pixel at xy from iChannel0
 	fragColor = vec4( smoothstep(0.1, 0.8, texColor.r+texColor.b+texColor.g - mxy.x) );
}
