// The inverse function of the smoothstep y(x) = x²(3-2x) has analytic inverse. Cubic as 
// it is, it involves some trigonometric computations. Surely enough that can be approximated
// but that's not the point of this shader. Computing the exact inverse is.
// 
// In yellow, the inverse_smoothstep. In gray, the smoothstep of the inverse_smoothstep, 
// which gives the identity. In blue, the smoothstep for reference and symmetry of the image.


// inverse of y = x²(3-2x), which is the usual smoothstep()
float inverse_smoothstep( float x )
{
    x = 1.0 - 2.0*x;

    //
    // TinyTexel version (see comments)
    //
    return 0.5 - sin(asin(x)/3.0);

    //
    // iq version
    //
    //float k = cos(acos(x)/3.0);             //  <----- need alien mathematics to solve this
    //return (1.0 + sqrt(3.0-k*k*3.0) - k)/2.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    float px = 1.0/iResolution.y;
    vec2  p = ( vec2((iResolution.y-iResolution.x)/2.0,0.0)+fragCoord)/iResolution.y;

    vec3 col = vec3(0.0);

    if( p.x>0.0 && p.x<1.0 )
    {
        col = vec3( 0.2 ) + 0.01*mod(floor(p.x*20.0)+floor(p.y*20.0),2.0);

        // blue: smoothstep
        float y1 = smoothstep( 0.0, 1.0, p.x );
        col = mix( col, vec3(0.0,0.4,0.8), 1.0-smoothstep( 0.0, 2.0*px, abs(p.y-y1) ) );

        // yellow: inverse_smoothstep
        float y2 = inverse_smoothstep( p.x );
        col = mix( col, vec3(1.0,0.7,0.0), 1.0-smoothstep( 0.0, 2.0*px, abs(p.y-y2) ) );

        // gray: smoothstep of inverse_smoothstep, back to identity
        float y3 = smoothstep( 0.0, 1.0, y2 );
        col = mix( col, vec3(0.5,0.5,0.5), 1.0-smoothstep(0.0, 1.5*px, abs(p.y-y3) ) );
    }

    fragColor = vec4( col, 1.0 );
}
