vec3 orange = vec3(.871,.331,.112);
vec3 skin   = vec3(.925,.662,.61);
vec3 sofa   = vec3(.709,.466,.415);

vec3 night  = vec3(.051,.037,.123);
vec3 sky    = vec3(.0,.5,.7);
vec3 milk   = vec3(.999,.999,.89);

vec3 gurk   = vec3(.1,.9,.2);
vec3 oldg   = vec3(.3,.8,.2);
vec3 gras   = vec3(.2,.9,.1);


void mainImage( out vec4 O, in vec2 U )
{
	vec2 uv = U.xy / iResolution.xy;

    vec3 c = vec3(1.,1.,1.);

    float t = 0.0;
    if(uv.x > 0.37){

      if( uv.y > .1 ) c = orange;
      if( uv.y > .2 ) c = skin;
      if( uv.y > .3 ) c = sofa;

      if( uv.y > .4 ) c = night;
      if( uv.y > .5 ) c = sky;
      if( uv.y > .6 ) c = milk;

      if( uv.y > .7 ) c = gurk;
      if( uv.y > .8 ) c = oldg;
      if( uv.y > .9 ) c = gras;



    }else{

      c = mix(sofa, skin, uv.y);

    }

    O = vec4(c,1.0);
}
