void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragCoord.y += sin(iTime+fragCoord.x*0.00125)*80.;

    float heigth = iResolution.y/2.;

    if(fragCoord.y<heigth)
    {
        fragColor=vec4(0.,fragCoord.y/heigth,0.85,1.);
    }
    else
    {
        fragColor=vec4(0.);
    }
}
