// From https://www.shadertoy.com/view/MdsXWM

const float samples = 6.0;

vec3 hsv2rgb (in vec3 hsv) {
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	return hsv.z * (1.0 + 0.5 * hsv.y * (cos (2.0 * 3.14159 * (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0))) - 1.0));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
	vec2 p = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
	p.x += 0.05 * cos (iTime + p.y * 20.0);
	vec3 color = vec3(0.0, 0.0, 0.2);
	float r = texture (iChannel0, vec2 (0.0)).x * 0.4;
	for(float d = 0.0; d < 1.0; d += 1.0 / samples) {
		vec3 c = hsv2rgb (vec3 (d + iTime, 1.0, 1.0));
		float v = texture (iChannel0, vec2 (d, 0.0)).x * 0.7;
		float a = 2.0 * 3.14159 * d + iTime;
		vec2 o = r * vec2 (cos (a), sin (a));
		color += c * v / length (p - o);
	}
	color /= samples;
	color = mix (vec3 (length (color)), color, smoothstep (r + 0.02, r + 0.05, length (p)));
	fragColor = vec4 (pow (color, vec3 (0.6 + r)), 1.0);
}
