void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 waveVector = 30.0 * vec2(0.4+cos(iTime), 0.3+sin(iTime));
    float phase = dot(uv, waveVector);
    float realPart = cos(phase);
    float imagPart = sin(phase);
    fragColor = vec4(realPart * 0.5 + 0.5, imagPart * 0.5 + 0.5, 0.0, 1.0);
}
