/*

Spinning Tiles
Copyright 2017 Theron Tarigo

Rotating texture feedback on cells in a grid

*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 s = texture(iChannel0, uv).rg;
    fragColor.rgb = vec3( s.r - s.g )*0.125+0.5;
}

/*

Spinning Tiles
Copyright 2017 Theron Tarigo

Rotating texture feedback on cells in a grid

*/

float squaresize;

ivec2 squaresel(vec2 frc) {
    return ivec2(floor(frc/squaresize+vec2(0.,0.5)));
}

vec2 squareuv(vec2 frc) {
    return fract(frc/squaresize+vec2(0.,0.5))*2.-1.;
}

vec4 squaresamp(ivec2 sq, vec2 uv) {
    return texture(iChannel0, (((uv*0.5+0.5)+vec2(sq)-vec2(0.,0.5))*squaresize)/iResolution.xy + vec2(0.5));
}

bool squarefit(ivec2 sq) {
    vec2 d = (abs(vec2(sq)+vec2(0.5,0.))+vec2(0.5,0.5))*squaresize - 0.5*iResolution.xy;
    return max(d.x,d.y)<=0.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    squaresize = 120.*pow(iResolution.y/1080.,0.5);
    vec2 frc = fragCoord.xy - 0.5*iResolution.xy;
    ivec2 sq = squaresel(frc);
    vec2 uv = squareuv(frc);
    fragColor.a = 1.;
    fragColor.rgb = vec3(0.);
    if (!squarefit(sq)) return;
    float a = 0.2*(float(iFrame)/(60.+float(sq.x))-float(sq.y));
    mat2 trans = 1.2*mat2(cos(a),-sin(a),sin(a),cos(a));
    fragColor.rgb = 0.9*squaresamp(sq, trans*uv).rgb;
    ivec2 cb = sq+100-((sq+100)/2)*2;
    fragColor.r += (cb.x==cb.y ? 0.5 : 0.0);
    fragColor.g += (cb.x!=cb.y ? 0.5 : 0.0);
}
