void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float i = iTime*.1;
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv*=4.0;
    uv-= 2.0;
    uv = abs(uv)-length(uv);

    float time = iTime+uv.y*.4+uv.x*.4;
    float f = sin(6.2831*240.0*time+sin(6.2831*time)*40.0);
    f *= pow(sin(time*80.0+cos(time*20.0)*50.0)*.5+.5,5.0);
    vec2 s =  vec2(sin(iTime*2.0),cos(iTime*2.0))*.1;

    uv+=i;
    uv = abs(uv);
    vec4 c1 = texture(iChannel0,uv);
    vec4 c2 = texture(iChannel1,uv);
    c1.r = s.x;
    c2.g = s.y;
    c2.b = f*.4+.5;
    c1 = normalize(abs(c1-c2));
    c1.b = f*.4+.5;
	fragColor = c1;
}

vec2 mainSound(float time){
    float f = sin(6.2831*240.0*time+sin(6.2831*time)*40.0);
    f *= pow(sin(time*80.0+cos(time*20.0)*50.0)*.5+.5,5.0);
    return vec2(sin(time*2.0)*f,cos(time*2.0)*f);
}
