vec4 sampleImage(sampler2D sampler, in vec2 uv)
{
    //return texture( sampler, uv );

	const float res = 512.0;
	uv = uv*res + 0.5;
    uv = floor( uv ) + smoothstep(0.0, 1.0, fract( uv ));
	uv = (uv - 0.5)/res;

    return texture( sampler, uv );
}



vec4 getCol(in vec2 uv) {
    uv = vec2(mod(uv.x, 1.0), mod(uv.y, 1.0));
    const float frameRes = 512.0;
    vec2 pp = uv * frameRes * 2.0;
    float uv_x, uv_y = 0.0;

    vec4 col = vec4(1.0,0.0,0.0,1.0);


    if (pp.x > 0.5 && pp.x <= (frameRes - 0.5)) {
        uv_x = (pp.x ) / (frameRes);

        //0,0
    	if (pp.y > 0.5 && pp.y <= (frameRes - 0.5)) {
        	uv_y = (pp.y ) / (frameRes );
            col = sampleImage(iChannel0, vec2(uv_x, uv_y));
    	}

       	//0,1
    	else if (pp.y > 0.5 + frameRes && pp.y <= (frameRes * 2.0 - 0.5)) {
        	uv_y = (pp.y - ( frameRes)) / (frameRes );
            col = sampleImage(iChannel2, vec2(uv_x, uv_y));
    	}

        //seams


    	if (pp.y > frameRes * 2.0 - 0.5 || pp.y <= 0.5) {
            float m = pp.y + 0.5;
            if (pp.y > frameRes * 2.0 - 0.5) {
                m = pp.y -frameRes * 2.0 + 0.5;
            }
            //m = smoothstep(0.0, 1.0, m);

            vec4 col1 = sampleImage(iChannel2, vec2(uv_x, 1.0));
            vec4 col2 = sampleImage(iChannel0, vec2(uv_x, 0.0));
            col = mix(col1, col2,m);
    	}



    	else if (pp.y > frameRes - 0.5 && pp.y <= frameRes + 0.5) {
            float m = pp.y - frameRes + 0.5;
            //m = smoothstep(0.0, 1.0, m);

            vec4 col1 = sampleImage(iChannel2, vec2(uv_x, 0.0));
            vec4 col2 = sampleImage(iChannel0, vec2(uv_x, 1.0));
            col = mix(col2, col1, m);
    	}



    }


    else if (pp.x > frameRes + 0.5 && pp.x <= (frameRes * 2.0 - 0.5)) {
        uv_x = (pp.x - (frameRes)) / (frameRes);

        //1,0
    	if (pp.y > 0.5 && pp.y <= (frameRes - 0.5)) {
        	uv_y = (pp.y) / (frameRes);
            col = sampleImage(iChannel1, vec2(uv_x, uv_y));
    	}

        //1,1
    	else if (pp.y > 0.5 + frameRes && pp.y <= (frameRes * 2.0 - 0.5)) {
        	uv_y = (pp.y - (frameRes)) / (frameRes );
            col = sampleImage(iChannel3, vec2(uv_x, uv_y));
    	}

    	//seams
    	else if (pp.y > frameRes * 2.0 - 0.5 || pp.y <= 0.5) {
            float m = pp.y + 0.5;
            if (pp.y > frameRes * 2.0 - 0.5) {
                m = pp.y -frameRes * 2.0 + 0.5;
            }
            //m = smoothstep(0.0, 1.0, m);

            vec4 col1 = sampleImage(iChannel3, vec2(uv_x, 1.0));
            vec4 col2 = sampleImage(iChannel1, vec2(uv_x, 0.0));
            col = mix(col1, col2,m);
    	}



    	else if (pp.y > frameRes - 0.5 && pp.y <= frameRes + 0.5) {
            float m = pp.y - frameRes + 0.5;
            //m = smoothstep(0.0, 1.0, m);

            vec4 col1 = sampleImage(iChannel3, vec2(uv_x, 0.0));
            vec4 col2 = sampleImage(iChannel1, vec2(uv_x, 1.0));
            col = mix(col2, col1, m);
            //col = mix(col, vec4(1.0,0.0,0.0,1.0), 0.5);
    	}

    }



    //seams

    else if (pp.x > frameRes * 2.0 - 0.5 || pp.x <= 0.5) {

    	if (pp.y > 0.5 && pp.y <= frameRes - 0.5) {
            uv_y = (pp.y ) / (frameRes );
    	    float m = pp.x + 0.5;
    	    if (pp.x > frameRes * 2.0 - 0.5) {
    	        m = pp.x -frameRes * 2.0 + 0.5;
    	    }
            //m = smoothstep(0.0, 1.0, m);

    	    vec4 col1 = sampleImage(iChannel1, vec2(1.0, uv_y));
    	    vec4 col2 = sampleImage(iChannel0, vec2(0.0, uv_y));
    	    col = mix(col1, col2,m);
    	}


    	else if (pp.y > frameRes + 0.5 && pp.y <= frameRes * 2.0 - 0.5) {
            uv_y = (pp.y - (frameRes)) / (frameRes );
    	    float m = pp.x + 0.5;
    	    if (pp.x > frameRes * 2.0 - 0.5) {
    	        m = pp.x -frameRes * 2.0 + 0.5;
    	    }
            //m = smoothstep(0.0, 1.0, m);

    	    vec4 col1 = sampleImage(iChannel3, vec2(1.0, uv_y));
    	    vec4 col2 = sampleImage(iChannel2, vec2(0.0, uv_y));
    	    col = mix(col1, col2,m);
    	}

        //corner


    	else if (pp.y > frameRes * 2.0 - 0.5 || pp.y <= 0.5) {
    	    float m = pp.x + 0.5;
    	    if (pp.x > frameRes * 2.0 - 0.5) {
    	        m = pp.x -frameRes * 2.0 + 0.5;
    	    }
            //m = smoothstep(0.0, 1.0, m);

            float m_y = pp.y + 0.5;
            if (pp.y > frameRes * 2.0 - 0.5) {
    			m_y = pp.y -frameRes * 2.0 + 0.5;
            }
    	    float m_x = pp.x + 0.5;
    	    if (pp.x > frameRes * 2.0 - 0.5) {
    	        m_x = pp.x -frameRes * 2.0 + 0.5;
    	    }
            //m_y = smoothstep(0.0, 1.0, m_y);
            //m_x = smoothstep(0.0, 1.0, m_x);

    	    vec4 col1 = sampleImage(iChannel1, vec2(1.0, 0.0));
    	    vec4 col2 = sampleImage(iChannel0, vec2(0.0, 0.0));
    	    vec4 col3 = sampleImage(iChannel2, vec2(0.0, 1.0));
    	    vec4 col4 = sampleImage(iChannel3, vec2(1.0, 1.0));

            vec4 col1x = mix(col1, col2, m_x);
    	    vec4 col2x = mix(col4, col3, m_x);
            col = mix(col1x, col2x, 1.0 - m_y);
    	}


    	if (pp.y > frameRes - 0.5 && pp.y <= frameRes + 0.5) {
    	    float m = pp.x + 0.5;
    	    if (pp.x > frameRes * 2.0 - 0.5) {
    	        m = pp.x -frameRes * 2.0 + 0.5;
    	    }

            float m_y = pp.y - frameRes + 0.5;
    	    float m_x = pp.x + 0.5;
    	    if (pp.x > frameRes * 2.0 - 0.5) {
    	        m_x = pp.x -frameRes * 2.0 + 0.5;
    	    }
            //m_y = smoothstep(0.0, 1.0, m_y);
            //m_x = smoothstep(0.0, 1.0, m_x);

    	    vec4 col1 = sampleImage(iChannel3, vec2(1.0, 0.0));
    	    vec4 col2 = sampleImage(iChannel2, vec2(0.0, 0.0));
    	    vec4 col3 = sampleImage(iChannel0, vec2(0.0, 1.0));
    	    vec4 col4 = sampleImage(iChannel1, vec2(1.0, 1.0));

            vec4 col1x = mix(col1, col2, m_x);
    	    vec4 col2x = mix(col4, col3, m_x);
            col = mix(col1x, col2x, 1.0 - m_y);
    	}
    }





    else if (pp.x > frameRes - 0.5 && pp.x <= frameRes + 0.5) {

    	if (pp.y > 0.5 && pp.y <= frameRes - 0.5) {
            uv_y = (pp.y ) / (frameRes );
    	    float m = pp.x - (frameRes + 0.5) + 1.0;
            //m = smoothstep(0.0, 1.0, m);

    	    vec4 col1 = sampleImage(iChannel1, vec2(0.0, uv_y));
    	    vec4 col2 = sampleImage(iChannel0, vec2(1.0, uv_y));
    	    col = mix(col2, col1, m);
    	}


    	if (pp.y > frameRes + 0.5 && pp.y <= frameRes * 2.0 - 0.5) {
            uv_y = (pp.y - (frameRes)) / (frameRes );
    	    float m = pp.x - (frameRes + 0.5) + 1.0;
            //m = smoothstep(0.0, 1.0, m);

    	    vec4 col1 = sampleImage(iChannel3, vec2(0.0, uv_y));
    	    vec4 col2 = sampleImage(iChannel2, vec2(1.0, uv_y));
    	    col = mix(col2, col1,m);
    	}


        //corner


    	if (pp.y > frameRes * 2.0 - 0.5 || pp.y <= 0.5) {
    	    float m = pp.x + 0.5;
    	    if (pp.x > frameRes * 2.0 - 0.5) {
    	        m = pp.x -frameRes * 2.0 + 0.5;
    	    }
            //m = smoothstep(0.0, 1.0, m);

            float m_y = pp.y + 0.5;
            if (pp.y > frameRes * 2.0 - 0.5) {
    			m_y = pp.y -frameRes * 2.0 + 0.5;
            }
    	    float m_x = pp.x - (frameRes + 0.5) + 1.0;
            //m_x = smoothstep(0.0, 1.0, m_x);

    	    vec4 col1 = sampleImage(iChannel0, vec2(1.0, 0.0));
    	    vec4 col2 = sampleImage(iChannel1, vec2(0.0, 0.0));
    	    vec4 col3 = sampleImage(iChannel3, vec2(0.0, 1.0));
    	    vec4 col4 = sampleImage(iChannel2, vec2(1.0, 1.0));

            vec4 col1x = mix(col1, col2, m_x);
    	    vec4 col2x = mix(col4, col3, m_x);
            col = mix(col1x, col2x, 1.0 - m_y);
    	}


    	if (pp.y > frameRes - 0.5 && pp.y <= frameRes + 0.5) {
    	    float m = pp.x + 0.5;
    	    if (pp.x > frameRes * 2.0 - 0.5) {
    	        m = pp.x -frameRes * 2.0 + 0.5;
    	    }

            //m = smoothstep(0.0, 1.0, m);
            float m_y = pp.y - frameRes + 0.5;
    	    float m_x = pp.x - (frameRes + 0.5) + 1.0;

            //m_x = smoothstep(0.0, 1.0, m_x);
            //m_y = smoothstep(0.0, 1.0, m_y);

    	    vec4 col1 = sampleImage(iChannel2, vec2(1.0, 0.0));
    	    vec4 col2 = sampleImage(iChannel3, vec2(0.0, 0.0));
    	    vec4 col3 = sampleImage(iChannel1, vec2(0.0, 1.0));
    	    vec4 col4 = sampleImage(iChannel0, vec2(1.0, 1.0));

            vec4 col1x = mix(col1, col2, m_x);
    	    vec4 col2x = mix(col4, col3, m_x);
            col = mix(col1x, col2x, 1.0 - m_y);
    	}
    }



    return col;

}

vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspectRatio = iResolution.x / iResolution.y;

	vec2 uv = fragCoord.xy / iResolution.xy;

    float zoom = (iTime - 12.0) /3.0 + 2.0;

    zoom = sin(zoom) * 4.0 + 2.0;
    float rotation = iTime / 5.0;
    vec2 origin = vec2(0.5, 0.5);


    float scale = 1.0 / pow(2.0, zoom);

    for (int i = 0; i < 1; i++){
    	fragColor = getCol(origin + rotate((uv - 0.5) * scale * vec2(aspectRatio, 1.0), rotation));
    	fragColor += vec4(vec3(min(0.0, iTime / 2.0 - 1.0)), 1.0);

        if (uv.x> 0.98) {
       		fragColor = vec4(1.0,1.0,0.0,1.0);

            float fps60 = iFrameRate / 60.0;

            if (uv.y < fps60) {
             	fragColor = vec4(1.0,0.0,0.0,1.0);   
            }
        }
    }


    //fragColor = vec4(pow((fragColor.r + fragColor.g + fragColor.b) / 3., sin(iTime) + 2.0));

}
