void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 coord = fragCoord.xy / iResolution.xy;
    vec3 color = vec3(0.6, 0.6, 0.0);
    vec2 q = coord - vec2(0.5, 0.5);

    float r = 0.25 + 0.03 * cos(atan(q.x, q.y) * 10.0 + iTime);
    color *= 1.5 - smoothstep(r, r + 0.3, length(q));

    color += 0.5 - smoothstep(r, r + 0.01, length(q));

    fragColor = vec4(color, 1.0);
}
