void mainImage( out vec4 o, in vec2 u ) {
    u -= iMouse.xy;

    float a = atan(u.y / u.x);
    float m = sqrt( dot(u, u) );

    vec2 xy = vec2(cos(a), sin(a)) * m * m / 100.0;
    vec2 mo = mod(iMouse.xy, 200.0);

    xy = xy * sign(u.x) + mo;
    xy = smoothstep( -0.1, 0.1, sin(xy / 10.0) );

	o = vec4(xy.x + xy.y);
}
