//کاری از سید مرتضی کمالی

#define step(a,b) smoothstep( a -2./iResolution.y , a +2./iResolution.y , b)
#define crescents( uvcoord,movement,maskcoord,radius,maskradius) vec3(max(0.,step(length(uvcoord+movement),radius)-step(length(uvcoord+movement+maskcoord),maskradius)))
#define Diamond(uv,radius)  vec3(step( uv.x, abs(uv.y)-radius))


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (2.*fragCoord - iResolution.xy ) / iResolution.y;

    //Big Crescents symmetry
    vec2 uvs = uv*vec2(-1,1); 
    vec3 BigCrescents = crescents(uv, vec2(.1,0),vec2(.17,-.1),.8,.8) + crescents(uvs,vec2(.1,0),vec2(.17,-.1),.8,.8); 

    //Small Crescents symmetry
  	uv.x = -abs(uv.x);    
 	vec3 SmallCrescents = crescents(uv,vec2(.35,-.1),vec2(-.28,.1),.65,.78);


  	//Middle
  	vec3 Middle = 1.0-vec3(min(1.0,Diamond((uv+vec2(0,0.05)),0.85 ).y + step( uv.x, -.1)));

    //Up
    vec3 Up = crescents(uv,vec2(.05,-.93),vec2(0,-.09),.1,.1);


  fragColor = vec4(SmallCrescents + Middle + BigCrescents +  Up ,1);
}
