/* [SH17A] Lorenz attractor, by mattz
   Licence: https://creativecommons.org/licenses/by-nc-sa/3.0/
*/


#define f(x) texelFetch(iChannel0, ivec2(x), 0)

void mainImage( out vec4 c, vec2 p ) {

    c = f(p);

    p.x -= 250.;

    c += p.y > 1. ?

        f(0)/exp(2.+length(p - 5.*f(0).yx)) - c/4e2 :

    vec4(c.z*c.y - 2.6*c.x,
         10.*(c.z-c.y) + .1,
         c.y*(28.-c.x)-c.z,
         0)/1e2;

}
