#define BorderSmoothness .2
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    uv.x*=iResolution.x/iResolution.y;
    uv*=10.;

    vec2 mouse=iMouse.xy/iResolution.xy*2.-1.;
    mouse.x*=iResolution.x/iResolution.y;
    mouse*=10.;

    float col=cos(length(uv)*2.)/2.+0.5;
    float col2=cos(length(uv-mouse)*2.)/2.+0.5;

    fragColor = vec4(smoothstep(1.-BorderSmoothness,1.+BorderSmoothness,col+col2));
}
