/**
 *
 * Chrome extension for Shadertoy:
 * 
 * http://bit.ly/shadertoy-plugin 
 * 
 */

void mainImage( out vec4 C, in vec2 R )
{
	R = ceil((R.xy / iResolution.xy - .5) * 99.) / 99. + cos(iTime) * .2;
    R.x *= 1. + pow(sin(iTime + R.y * 2.), 4.);
    //C -= C; // mac fix - clear color;
    R *= sin(R * 30.);
    C += -C + ceil(R.x * R.y);
}

