// See Buf A
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {  
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 c = texture(iChannel0, uv);
    vec4 c2 = texture(iChannel0, uv + vec2(0.002, 0.002));
    c = c - distance(c, c2);

    fragColor = vec4(c.rgb, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord.xy / iResolution.xy;    
    vec3 c = texture(iChannel0, uv).rgb;

    float d = 0.001 / c.g;
    float a = c.r * 15.0 + c.b * 10.0 + sin(iTime*0.001);
    uv.x += d * cos(a);
    uv.y += d * sin(a);

    c = texture(iChannel1, uv).rgb * 0.995  // buffer
      + texture(iChannel0, uv).rbg * 0.005; // webcam

    c.r = pow(mod(c.r * 1.003 , 1.0), 1.003);
    c.g = pow(mod(c.g * 1.004 , 1.0), 1.004);
    c.b = pow(mod(c.b * 1.005 , 1.0), 1.005);


    fragColor = vec4(c, 1.0);
}
