// Specialization of https://www.shadertoy.com/view/XsXSz4
//
// Since it's equlatearal, symmetry can be exploited to make the solution cheaper


// signed distance to an equilateral triangle
float sdEquilateralTriangle(  in vec2 p )
{
    const float k = sqrt(3.0);

    p.x = abs(p.x) - 1.0;
    p.y = p.y + 1.0/k;

    if( p.x + k*p.y > 0.0 ) p = vec2( p.x - k*p.y, -k*p.x - p.y )/2.0;

    p.x -= clamp( p.x, -2.0, 0.0 );

    return -length(p)*sign(p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    p *= 2.0;

	float d = sdEquilateralTriangle( p );

    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.8 + 0.2*cos(120.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );


	fragColor = vec4(col,1.0);
}
