float root(float num) {
    float c = 0.0;

    for (float i = 1.0; i < 100.0; i+=1.0) {
        if (num > 0.0) {
            num = num - c*2.0-1.0;
            c += 1.0;
        }
    }
    return c+num/(c*2.0-1.0)*(sin(iTime)*0.5+0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = vec4((root(fragCoord.x*0.02)-fragCoord.y*0.02)*20.0);
}
