float cRange = 256.00/12.0;
float cValue = 0.0;
float cColor = 0.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, fragCoord.xy / iResolution.xy);

    cColor = 0.5 + 0.5*sin(iTime);
    cValue = max(max(fragColor.x,fragColor.y),fragColor.z);

    fragColor.x = mix(fragColor.x,cValue,cColor);
    fragColor.y = mix(fragColor.y,cValue,cColor);
    fragColor.z = mix(fragColor.z,cValue,cColor);
}
